def get_area_server (area = '-1', server = '-1'):
    server_name = ''
    server_list = []
    if area != '-1' and server != '-1':#one server
        for each_server in dict_s[area][1]:
            if each_server[0] == int(server):
                server_name = each_server[1]
                break
        server_list.append([area, server, server_name])
    elif area != '-1' and server == '-1':#all server in one area
        for each_server in dict_s[area][1]:
            server_id = str(each_server[0])
            server_name = each_server[1]
            server_list.append([area, server_id, server_name])
    elif area == '-1' and server == '-1':#all area
        for each in dict_s:
            for each_server in dict_s[each][1]:
                server_id = str(each_server[0])
                server_name = each_server[1]
                server_list.append([each, server_id, server_name])

    deleted_server_list = del_exiet_server(server_list)
    return deleted_server_list

